import React from 'react'
import Card from './Card';
import data from './Services_data';     //importing data from services_data.jsx 



const make_payment=async()=>{
  const stripe = await loadStripe('pk_test_51NkQDHSG1F80rKm2dNFFXW0WIc7Vu6UEH9cfyeDKdBmNV27UyBPjn2pBP5CNvUJuK4pCQRh0ikJS4ZrdXovdUpOd00WSe7sL1E');
}

const Skills = () => {
  return (
    <>
    <div className='dibba'>

    {/* this is simple approach to passing props manually */}
    {/* here we are calling cards again and again  */}
        
      {/* <Cards name='sid' img='images/download.png'/>
      <Cards name='sid1' img='images/images.jpg'/>
      <Cards name='sid2' img='images/download.png'/> */}



        {/*this is another approach using map method for getting all the data in services_data */}
      {data.map((val,ind,arr)=>{
        return <Card name={val.name} image={val.image} />
      })}

        <button onClick={make_payment}>Make Payment</button>
    </div>
    </>
  )
}

export default Skills
