import React, { useState,useEffect } from 'react'
import { useNavigate } from 'react-router-dom';

const Contact = () => {
  const [userData,setUserData]=useState({name:"",email:"",phone:"",message:""});
  const navigate=useNavigate();
  
  useEffect(()=>{
    callAboutPage();  // can't use async function in useeffect hook
  },[])
  
  // authenticate using cookie for this use cookie parser

  const callAboutPage=async()=>{
    try{
 
      const res=await fetch("http://localhost:5000/contact",{
        method:"GET",
        headers:{
          Accept:"application/json", 
          "Content-Type":"application/json"
        },
        credentials:"include"
      });

      
      const data=await res.json(); 
      // console.log(data);
      // console.log(data.name);
      setUserData(...userData,{name:data.name,email:data.email,phone:data.phone});


    }
    catch(err){

      console.log(err);
      navigate("/login"); 

    }
  }  

  const handleInput=(e)=>{
    const name=e.target.name;
    const value=e.target.value; 

    setUserData(...userData )
  }

  return (
    <>
    <div className='contact_container'>
      {/* <h1>hello contact page : </h1> */}
      <div className='contact_container1'>
        <p>name</p>
        <p>SIDDHANT JAIN</p>
      </div>
      <div  className='contact_container1'>
        <p>phone</p>
        <p>9301402374</p>
      </div>
      <div className='contact_container1'>
        <p>email</p>
        <p>bjsiddhu2003@gmail.com</p>
      </div>
    </div>

    <div className='contact_container2'>
      {/* hello
       */}
       <h1>Get In Touch</h1>
       <input type="text" placeholder={userData.name}  namevalue={userData.name} onChange={handleInput} name='fname1' />
       <input type="text" placeholder={userData.email} name value={userData.email} onChange={handleInput}name='fname1' />
       <input type="text" placeholder={userData.phone} namevalue={userData.phone} onChange={handleInput} name='fname1' />
       <br />
       {/* <input type="text" placeholder='Your Phone no. : ' name='fname1' /> */}
       <textarea name="message" id="hello" cols="70" rows="5"  namevalue={userData.message} onChange={handleInput}placeholder='Message : '></textarea>
       <br />
       <button className='contact_btn'>send msg</button>
    </div>
    </>
  )
}

export default Contact
