import React, { useState } from 'react'
import {useNavigate } from 'react-router-dom';

const Register = () => {

  const navigate=useNavigate();    //used to navigate the pages

  const [setdata,setFullData]=useState({})        //here we are using usestate hook for getting data after user filled data in registration page 

  function entered(event){
    const value=event.target.value;     
    const name1=event.target.name;

    setFullData({
      ...setdata,
      [name1]:value
    })
  }

    const logined=async()=>{
      navigate('/login');
    }

  const submitted=async(e)=>{     //e ek event hai 

    
      // e.preventDefault();     //form ko refresh hone se rokega

      // fetch method for accessing post method on app2.js 
      
      const res=await fetch('http://localhost:5000/register',{
        method:'POST',
        body:JSON.stringify({
          name:setdata.name,
          age:setdata.age,
          phone:setdata.phone,
          email:setdata.email,
          password:setdata.password,
          cpassword:setdata.cpassword
  
        }),
        headers:{
          "Content-Type" : "application/json"
        },
      })
      

      const data=await res.json();
      console.log(data);

      if(res.status===400 ){
        alert(data.error);
        e.preventDefault();     //form ko refresh hone se rokega
      }
      else{
        alert(data.msg)
        console.log('success registration');
        navigate('/login')
      }
  
      
    }

  

  return (
    <div className='contact_form'>
      <form  onSubmit={submitted}>
  <div className="mb-4 input_name"> 
    <input type="text" placeholder='Full Name' className="form-control input_tags" name='name' onChange={entered} id="exampleInputEmail1" aria-describedby="emailHelp" />
  </div>
  <div className="mb-4">
   
    <input type="text" placeholder='Age' className="form-control input_tags" id="exampleInputPassword1" name='age' onChange={entered}/>
  </div>
  <div className="mb-4">

    <input type="text" placeholder='Mobile No.' className="form-control input_tags" id="exampleInputPassword1" name='phone' onChange={entered}/>
  </div>
  <div className="mb-4">
    <input type="text" placeholder='Email ID' className="form-control input_tags" id="exampleInputPassword1" name='email' onChange={entered}/>
  </div>
  <div className="mb-4">
    <input type="text" placeholder='Password' className="form-control input_tags" id="exampleInputPassword1" name='password' onChange={entered}/>
  </div>
  <div className="mb-4">
    <input type="text" placeholder='Confirm Password' className="form-control input_tags" id="exampleInputPassword1" name='cpassword' onChange={entered}/>
  </div>
  <button type="submit" className="btn mt-4 mb-5 btn-primary" >Submit</button>
  <p onClick={logined} className='register_create'>Already register ?</p>
</form>
    </div>
  )
}

export default Register;
