import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom'

const About = () => {

  const [userData,setUserData]=useState();

  const navigate=useNavigate();
  
  useEffect(()=>{
    callAboutPage();  // can't use async function in useeffect hook
  },[])
  


  // authenticate using cookie for this use cookie parser

  const callAboutPage=async()=>{
    try{
      // console.log("about page try block");  
      const res=await fetch("http://localhost:5000/about",{
        method:"GET",
        headers:{
          Accept:"application/json", 
          "Content-Type":"application/json"
        },
        credentials:"include"
      });
      // console.log("about page try block1");
      
      const data=await res.json(); 
      // console.log(data);
      // console.log(data.name);
      setUserData(data);
      // console.log("about page try block2");


      // if(!res.status===200){
      //   const error=new Error(res.error);
      //   throw error;
      // }

      if(data.status===200 || data){
        return 1;
      }
    }
    catch(err){
      // console.log("about page catch block1");
      // console.log(err);
      navigate("/login"); 
      // console.log("about page catch block2");
    }
    // console.log("about page main block");
  }  
  
  // simple html code 
  return (
    <>
      <h1 className='about_heading'>Hello about page : </h1>
      {/* <p>{userData.name}</p>
      <p>{userData.email}</p>
      <p>{userData.age}</p>
      <p>{userData.phone}</p>
      <p>{userData.name}</p>
      <p>{userData.name}</p> */}
      <img className='about_img' src="../images/about.jpeg" alt="error" />
    </>
  )
}

export default About;
