import React, { useEffect, useState } from 'react'

const Carts =() => {
    const [userData,setUserData]=useState([]);    //in this we store the items to display in cart

    useEffect(()=>{     //when we open about page then callAboutPage is called once
      callCartItem();  // can't use async function in useeffect hook
    },[])

    const callCartItem=async()=>{
      const res=await fetch('http://localhost:5000/carts',{
      method:'GET',
      headers:{
        "Content-Type" : "application/json"
      },
      
      credentials:"include"
    })

    const data=await res.json()
    console.log(data);

    setUserData(data)
    }


    

    return (
      <>
        {userData.length===0 ?<h1>cart is empty</h1>:null}
      
        <div className='dibba' >
        {
          /* getdata.map((element,id)=>{ */
          userData.map((element,id)=>{
            return (
              <>
              <div class="card mt-5 ms-5" style={{width: '18rem'}}>
      <img src={element.image} class="card-img-top" style={{height:'10rem'}} alt="error" />
      <div class="card-body">
        <h5 class="card-title">{element.product_name}</h5>
        <p>Price :  â‚¹ {element.product_price}</p>
        <p class="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
        <p onClick={async()=>{
          const res=await fetch('http://localhost:5000/delete_product',{
      method:'POST',
      headers:{
        "Content-Type" : "application/json"
      },
      body:JSON.stringify({
          product_name:element.product_name
      }),
      
      credentials:"include"
    })
        }}>delete</p>

        {/* for increment  */}
        <p onClick={async()=>{
          const res=await fetch('http://localhost:5000/increment_product',{
      method:'POST',
      headers:{
        "Content-Type" : "application/json"
      },
      body:JSON.stringify({
          product_name:element.product_name
      }),
      
      credentials:"include"
    })
        }} >increment : <span>{element.quantity}</span></p>

        {/* for decrement  */}
        <p onClick={async()=>{
          const res=await fetch('http://localhost:5000/decrement_product',{
      method:'POST',
      headers:{
        "Content-Type" : "application/json"
      },
      body:JSON.stringify({
          product_name:element.product_name
      }),
      
      credentials:"include"
    })
        }} >decrement : <span>{element.quantity}</span></p>
      
      </div>
    </div>


    <div>
        <button onClick={make_payment}>Make Payment</button>    
    </div>
              </>
            )
          })
        }
          
    
    
        </div>
     </> )

   
}

export default Carts
