import React, { useState,useEffect } from 'react'
import { useNavigate } from 'react-router-dom';

const Contact = () => {
  const [userData,setUserData]=useState({});
  const navigate=useNavigate();
  
  useEffect(()=>{
    callAboutPage();  // can't use async function in useeffect hook
  },[])
  
  // authenticate using cookie for this use cookie parser

  const callAboutPage=async()=>{
    try{
 
      const res=await fetch("http://localhost:5000/about",{
        method:"GET",
        headers:{
          Accept:"application/json", 
          "Content-Type":"application/json"
        },
        credentials:"include"
      });

      
      const data=await res.json(); 
      // console.log(data);
      // console.log(data.name);
      setUserData(data);


    }
    catch(err){

      console.log(err);
      navigate("/login"); 

    }
  }  

  return (
    <>
    <div className='contact_container'>
      {/* <h1>hello contact page : </h1> */}
      <div  className='contact_container1'>
        <p>phone</p>
        <p>9301402374</p>
      </div>
      <div className='contact_container1'>
        <p>email</p>
        <p>{userData.email}</p>
      </div>
      <div className='contact_container1'>
        <p>name</p>
        <p>{userData.name}</p>
      </div>
    </div>

    <div className='contact_container2'>
      {/* hello
       */}
       <h1>Get In Touch</h1>
       <input type="text" placeholder={userData.name} name='fname1' />
       <input type="text" placeholder={userData.email} name='fname1' />
       <input type="text" placeholder={userData.phone} name='fname1' />
       <br />
       {/* <input type="text" placeholder='Your Phone no. : ' name='fname1' /> */}
       <textarea name="message" id="hello" cols="70" rows="5" placeholder='Message : '></textarea>
       <br />
       <button className='contact_btn'>send msg</button>
    </div>
    </>
  )
}

export default Contact
