import React, { useState } from 'react'

const Login = () => {

  const [data,setdata]=useState({})

  function entered(event){
    const value=event.target.value;
    const name1=event.target.name;

    setdata({
      ...data,
      [name1]:value 
    })
  
  }

  async function submitted(event){
    // event.preventDefault();
    const res=await fetch('http://localhost:5000/login',{
      method:'POST',
      body:JSON.stringify({
        email:setdata.email,
        password:setdata.password,
      }),
      headers:{
        "Content-Type" : "application/json"
      },
    })
  }




  return (
    <div className='contact_form'>
      <form onSubmit={submitted} >
  <div className="mb-4 input_name"> 
    <input type="text" placeholder='Email ID' className="form-control input_tags" name='email' id="exampleInputEmail1" aria-describedby="emailHelp"  onChange={entered}/>
  </div>
  <div className="mb-4">
   
    <input type="text" placeholder='Password' className="form-control input_tags" id="exampleInputPassword1" name='password' onChange={entered}/>
  </div>
    
  <button type="submit" className="btn mt-4 mb-5 btn-primary" onClick={submitted}>Submit</button>

  <p>Create an account ?</p>
</form>
    </div>
  )
}

export default Login
