hbs.registerPartials(path.join(__dirname,"../templates/partials"));


// defining routes
app.get("/",(req,res)=>{
    res.render('index.hbs');
});
app.get("/login",(req,res)=>{
    res.render('login.hbs');
});
app.get("/registration",(req,res)=>{
