                email:req.body.email1,
                name:req.body.name1,
                password:req.body.password1,
                cpassword:req.body.cpassword1,
                age:req.body.age1,
                phone:req.body.phone1,
                // gender:req.body.gen,
                
            })


            // iske phele model me register.js me bcrypt ka code chalega


            const result=await detail.save();
            res.render('index.hbs');           
        }
        else{
            res.send('password not matched');
        }

    }
    catch(err){
        res.status(400).send(err)
    }
})


// post method for login page 
app.post('/login',async(req,res)=>{
    try{
        const email3=req.body.email2;
        const password3=req.body.password2;
        const login_detail=await UserDetail1.findOne({email:email3})
        
        const isMatch=await bcrypt.compare(password3,login_detail.password);
        console.log(isMatch);
        console.log(login_detail.password);
        console.log(password3);
        if(isMatch){
            res.render('index.hbs');          
        }
        else{
            res.send('invalid login details');
        }

    }
    catch(err){
        res.status(400).send("invalid login details 1 ");
    }
})


// listening server
app.listen(port,()=>{
    console.log(`server is running on port ${port}`);
})
