import React from 'react'

const Forgot_password = () => {

    const navigate=useNavigate();

  const [data,setdata]=useState({})             //here we are using usestate hook for storing data after person login

  function entered(event){

    // console.log(event.target);
    const value=event.target.value;
    const name1=event.target.name;

    setdata({
      ...data,
      [name1]:value 
    })

    // console.log(data);
  
  }
  const registered=async()=>{
    navigate('/register');
  }
  
  const forgot_passoword=async()=>{
    navigate('/forgot_password');
  }

  const submitted=async(event)=>{
    // console.log(setdata.email);
    event.preventDefault();       // form ko refersh hone se rokega
    const res=await fetch('http://localhost:5000/login',{
      method:'POST',
      credentials: 'include',
      body:JSON.stringify({
        email:data.email,
        password:data.password,
      }),
      headers:{
        "Content-Type" : "application/json"
      },
    })

    const data1=await res.json();
    console.log(data1);

    // console.log(res);

    if(res.status===400){
      // console.log('no login')
      alert(data1.error); 
    }
    else{
      // console.log('success login');
      alert(data1.msg);
      dispatch({type:'USER',payload:true})    //this is used in toggling login - logout and used in reducer folder
      navigate('/home')
      window.location.reload();
    }

  }

  return (
    <div className='contact_form'>
        <form  onSubmit={submitted}>
    <div className="mb-4 input_name"> 
      <input type="email" placeholder='Email ID' className="form-control input_tags" name='email' id="exampleInputEmail1" aria-describedby="emailHelp"  onChange={entered}/>
    </div>
    <div className="mb-4">    
      <input type="password" placeholder='Old Password' className="form-control input_tags" id="exampleInputPassword1" name='password' onChange={entered}/>
    </div>

    <div className="mb-4">    
      <input type="password" placeholder='New Password' className="form-control input_tags" id="exampleInputPassword1" name='password' onChange={entered}/>
    </div>
      
    <button type="submit" className="btn mt-4 mb-5 btn-primary">Submit</button>   
  </form>
      </div>
  )
}

export default Forgot_password
