import React, { useState } from 'react'
import data from './Services_data';     //importing data from services_data.jsx 
import { useDispatch } from 'react-redux'
import { ADD } from './redux/actions/action';

// this is a card component used in skills page 
const Card = () => {

  const [data1,setdata]=useState(data)  //simple array ka data lene ke liye 


  const dispatch=useDispatch()          

  const send=(e)=>{             //button onClick event fire here
    // console.log(e);
    dispatch(ADD(e))            //ADD call present in action.js and getting item as e
  }




const [userData,setUserData]=useState({});    //this usestate is used to store data of person after generation token and verify in auth middleware

  const navigate=useNavigate();   //used to navigate pages
  
  useEffect(()=>{     //when we open about page then callAboutPage is called once
    callAboutPage();  // can't use async function in useeffect hook
  },[])
  
  // authenticate using cookie for this use cookie parser

  const callAboutPage=async()=>{    //callAboutPage is simply go to app2.js in /about get request
    try{
 
      const res=await fetch("http://localhost:5000/about",{
        method:"GET",
        headers:{
          Accept:"application/json", 
          "Content-Type":"application/json"
        },
        credentials:"include"
      });

      
      const data=await res.json();    //data store person details coming from app2.js in get /about
      // console.log(data);
      // console.log(data.name);
      setUserData(data);


    }
    catch(err){
      console.log(err);
      navigate("/login"); 
    }
  }  
  














  return (
    <div className='dibba' >
    {
      data.map((element,id)=>{
        return (
          <>
          <div class="card mt-5 ms-5" style={{width: '18rem'}}>
  <img src={element.image} class="card-img-top" style={{height:'10rem'}} alt="error" />
  <div class="card-body">
    <h5 class="card-title">{element.name}</h5>
    <p>Price :  â‚¹ {element.price}</p>
    <p class="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
    {/* <a href="#" class="btn btn-primary" style={{width:'15rem' , marginLeft:'0.5rem'}} onClick={()=>send(element)}>Add to cart</a> */}
    <a href="#" class="btn btn-primary" style={{width:'15rem' , marginLeft:'0.5rem'}} onClick={async()=>{
      const res=await fetch('http://localhost:5000/cartDetails',{
        method:'POST',
        body:JSON.stringify({
          user_id:'002',
          product_id:"200",
          product_name:element.name,
          product_price:element.price,  
          messages:"hello prabhu kaise hai app"
        }),
        headers:{
          "Content-Type" : "application/json"
        },
      })
    }}>Add to cart</a>
  </div>
</div>
          </>
        )
      })
    }
      


    </div>
  )
}

export default Card
