import React, { useContext, useState } from 'react'
import { useNavigate } from 'react-router-dom';
import { UserContext } from './App';

const Login = () => {

  const {state,dispatch}=useContext(UserContext);   //using useContext hook for toggle login - logout

  const navigate=useNavigate();

  const [data,setdata]=useState({})             //here we are using usestate hook for storing data after person login

  function entered(event){

    // console.log(event.target);
    const value=event.target.value;
    const name1=event.target.name;

    setdata({
      ...data,
      [name1]:value 
    })

    // console.log(data);
  
  }
  const registered=async()=>{
    navigate('/register');
  }

  const submitted=async(event)=>{
    // console.log(setdata.email);
    event.preventDefault();       // form ko refersh hone se rokega
    const res=await fetch('http://localhost:5000/login',{
      method:'POST',
      credentials: 'include',
      body:JSON.stringify({
        email:data.email,
        password:data.password,
      }),
      headers:{
        "Content-Type" : "application/json"
      },
      // credentials:"include",
      // withCredentials: true
    })

    if(res.status===400){
      // console.log('no login')
      alert(res); 
    }
    else{
      // console.log('success login');
      alert(data.msg);
      dispatch({type:'USER',payload:true})    //this is used in toggling login - logout and used in reducer folder
      navigate('/home')
    }


  }




  return (
    <div className='contact_form'>
      <form  onSubmit={submitted}>
      {/* <p>{JSON.stringify(data)}</p> */}
  <div className="mb-4 input_name"> 
    <input type="text" placeholder='Email ID' className="form-control input_tags" name='email' id="exampleInputEmail1" aria-describedby="emailHelp"  onChange={entered}/>
  </div>
  <div className="mb-4">
   
    <input type="text" placeholder='Password' className="form-control input_tags" id="exampleInputPassword1" name='password' onChange={entered}/>
  </div>
    
  <button type="submit" className="btn mt-4 mb-5 btn-primary">Submit</button>

  <p onClick={registered} className='login_create'>Create an account ?</p>
</form>
    </div>
  )
}

export default Login
