import React, { useState } from 'react'

const Contact = () => {
  const [setdata,setFullData]=useState({
    fname:'',
    age:'',
    mobile:'',
    email:'',
    password:'',
    cpassword:''
  })

  function entered(event){
    const value=event.target.value;
    const name1=event.target.name;

    setFullData((preValue)=>{
      return{
        ...preValue,
        [name1]:value 
      }
    })
  }

  function submitted(){
    return(
      <>     
      {alert(`Your name is : ${setdata.fname} and your age is : ${setdata.age} , your mobile no. is : ${setdata.mobile} and your email id is : ${setdata.email} and your email id is : ${setdata.password } and your email id is : ${setdata.cpassword}`)}
      </>
    )
  }

  return (
    <div className='contact_form'>
      <form >
  <div className="mb-4 input_name"> 
    <input type="text" placeholder='Full Name' className="form-control input_tags" name='fname' onChange={entered} id="exampleInputEmail1" aria-describedby="emailHelp" />
  </div>
  <div className="mb-4">
   
    <input type="text" placeholder='Age' className="form-control input_tags" id="exampleInputPassword1" name='age' onChange={entered}/>
  </div>
  <div className="mb-4">

    <input type="text" placeholder='Mobile No.' className="form-control input_tags" id="exampleInputPassword1" name='mobile' onChange={entered}/>
  </div>
  <div className="mb-4">
    <input type="text" placeholder='Email ID' className="form-control input_tags" id="exampleInputPassword1" name='email' onChange={entered}/>
  </div>
  <div className="mb-4">
    <input type="text" placeholder='Password' className="form-control input_tags" id="exampleInputPassword1" name='password' onChange={entered}/>
  </div>
  <div className="mb-4">
    <input type="text" placeholder='Enter confirm password : ' className="form-control input_tags" id="exampleInputPassword1" name='cpassword' onChange={entered}/>
  </div>
  <button type="submit" className="btn mt-4 mb-5 btn-primary" onClick={submitted}>Submit</button>
</form>
    </div>
  )
}

export default Contact;
